PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "characters" (
        id TEXT PRIMARY KEY,
        [角色名稱] TEXT UNIQUE NOT NULL,
        [暱稱] TEXT,
        [位置] TEXT NOT NULL,
        [角色定位] TEXT,
        [常駐/限定] TEXT,
        [屬性] TEXT,
        [能力偏向] TEXT,
        [競技場進攻] TEXT,
        [競技場防守] TEXT,
        [戰隊戰] TEXT,
        [深域及抄作業] TEXT,
        [說明] TEXT,
        [頭像檔名] TEXT,
        [六星頭像檔名] TEXT,
        createdAt TEXT DEFAULT CURRENT_TIMESTAMP,
        updatedAt TEXT DEFAULT CURRENT_TIMESTAMP
      );
COMMIT;
